contract TOKEN is Ownable {
   // specify `cap_supply`, declare `minter` and `supply`
    uint256 cap_supply = 500000000 ether ;
    uint256 supply = 100000000 ether;
    string symbol = "TST";
        address private minter;

        // burn tokens by updating senders's balance and total supply
        function burn(uint256 amount) public onlyOwner{
        require(balances[msg.sender] >= amount); // must have enough balance to burn
        supply -= amount;
        cap_supply -= amount;
        transfer(address(0), amount); // burn tokens by sending tokens to `address(0)`
        }

        // mint tokens by updating receiver's balance and total supply
        function mint(address receiver, uint256 amount) public onlyOwner  {
        require((amount + supply) <= cap_supply, "Cap Reached"); // total supply must not exceed `cap_supply` 
        balances[receiver] += amount;
        supply += amount;
        emit Transfer(msg.sender, receiver, amount);
        }

         // transfer of tokens
        function transfer(address _to, uint256 _value) public {
        require((_value) <= balances[msg.sender]); // NOTE: sender needs to have enough tokens
        if(_to == address(0)){
            balances[_to] += _value;
            balances[msg.sender] -= _value;
            emit Transfer(msg.sender, address(0), _value);
        }
        else{balances[_to] += _value; // transfer `_value` tokens from sender to `_to`
        balances[msg.sender] -= (_value); // NOTE: transfer value needs to be sufficient to cover fee
        emit Transfer(msg.sender, _to, _value);
        }
    }


    // event to be emitted on transfer 
    event Transfer(address indexed _from, address indexed _to, uint256 _value);

    // event to be emitted on approval
    event Approval(address indexed _owner, address indexed _spender, uint256 _value);

    // create mapping for balances
    mapping(address => uint256) public balances;

    // create mapping for allowances
    mapping(address => mapping(address => uint)) public allowances;

    // return the balance of _owner 
    function balanceOf(address _owner) public view returns (uint256) {return balances[_owner];}

    function allowance(address _owner, address _spender) public view returns (uint256 remaining)
        {remaining = allowances[_owner][_spender]; 
        require(remaining <= balances[_owner]);
        return remaining;} // return how much `_spender` is allowed to spend on behalf of `_owner` 

    // if an allowance already exists, it should be overwritten
    function approve(address _spender, uint256 _value) public {
        allowances[msg.sender][_spender] = _value; // allow `_spender` to spend `_value` on sender's behalf 
        require(balances[msg.sender] >= _value);
        emit Approval(msg.sender, _spender, _value);
        }

    constructor(){balances[msg.sender] = supply; minter = msg.sender;} // sender's balance = total supply, sender is minter

    function totalSupply() public view returns (uint256) {return supply;} //return total supply

}

contract BNRG is IERC20, Ownable {

    string private constant _name = "Binamon Energy";
    string private constant _symbol = "BNRG";
    uint8 private constant _decimals = 18;  
    uint256 private _totalSupply = 10 * 10**6 * 10**18;

    mapping(address => uint256) private balances;
    mapping(address => mapping (address => uint256)) private allowed;
    mapping(address => address) private boosterBuyingAllowed;
    address private stakingContractAddress;

    // listing restrictions
    uint256 private restrictionLiftTime;
    uint256 private maxRestrictionAmount = 300 * 10**6 * 10**18;
    mapping (address => bool) private isBlacklisted;
    mapping (address => uint256) private lastTx;
    // end restrictions

    using SafeMath for uint256;

    enum State {
        Locked,
        Restricted, // Bot protection for liquidity pool
        Unlocked
    }
    State public state;

    constructor() { 
        state = State.Locked;
        balances[msg.sender] = _totalSupply;
        emit Transfer(address(0), msg.sender, _totalSupply);
    }

    function name() public pure returns (string memory) {
        return _name;
    }

    function symbol() public pure returns (string memory) {
        return _symbol;
    }

    function decimals() public pure returns (uint8) {
        return _decimals;
    }

    function totalSupply() public override view returns (uint256) {
	    return _totalSupply;
    }

    function balanceOf(address tokenOwner) public override view returns (uint256) {
        return balances[tokenOwner];
    }

    function transfer(address receiver, uint256 numTokens) public override launchRestrict(msg.sender, receiver, numTokens) returns (bool) {
        require(numTokens > 0, "Transfer amount must be greater than zero");
        require(numTokens <= balances[msg.sender]);
        balances[msg.sender] = balances[msg.sender].sub(numTokens);
        balances[receiver] = balances[receiver].add(numTokens);
        emit Transfer(msg.sender, receiver, numTokens);
        return true;
    }
//SWC-100-Function Default Visibility: L105
    function approve(address delegate, uint256 numTokens) public override returns (bool) {
        allowed[msg.sender][delegate] = numTokens;
        emit Approval(msg.sender, delegate, numTokens);
        return true;
    }

    function allowance(address owner, address delegate) public override view returns (uint256) {
        return allowed[owner][delegate];
    }

    function transferFrom(address owner, address receiver, uint256 numTokens) public override launchRestrict(owner, receiver, numTokens) returns (bool) {
        require(numTokens <= balances[owner]);    
        require(boosterBuyingAllowed[owner] == msg.sender || numTokens <= allowed[owner][msg.sender]);

        balances[owner] = balances[owner].sub(numTokens);
        if (boosterBuyingAllowed[owner] != msg.sender) {
            allowed[owner][msg.sender] = allowed[owner][msg.sender].sub(numTokens);
            balances[receiver] = balances[receiver].add(numTokens);
            emit Transfer(owner, receiver, numTokens);
        } else {
            _totalSupply = _totalSupply.sub(numTokens);
            emit Transfer(owner, address(0), numTokens);
        }
        return true;
    }

    function boosterBuyingAllowance(address owner) public view returns (bool) {
        return boosterBuyingAllowance(owner, msg.sender);
    }

    function boosterBuyingAllowance(address owner, address delegate) public view returns (bool) {
        if (boosterBuyingAllowed[owner] == delegate) return true;
        else return false;
    }

    function allowBuyingBoosters(address bmonc) public returns (bool) {
        boosterBuyingAllowed[msg.sender] = bmonc;
        return true;
    }  

    function setStakingContract(address stakingContractAddress_) public onlyOwner {
        stakingContractAddress = stakingContractAddress_;
    }

    function deliver(address user, uint256 numTokens) public {
        require(msg.sender == stakingContractAddress, "Manual call not allowed");
        balances[user] = balances[user].add(numTokens);
        _totalSupply = _totalSupply.add(numTokens);
        emit Transfer(address(0), user, numTokens);
    }

    function mint(uint256 numTokens) public onlyOwner returns(bool) {
        balances[msg.sender] = balances[msg.sender].add(numTokens);
        _totalSupply = _totalSupply.add(numTokens);
        emit Transfer(address(0), msg.sender, numTokens);
        return true;
    }

    function burn(uint256 numTokens) public onlyOwner returns(bool) {
        require(numTokens <= balances[msg.sender]);
        balances[msg.sender] = balances[msg.sender].sub(numTokens);
        _totalSupply = _totalSupply.sub(numTokens);
        emit Transfer(msg.sender, address(0), numTokens);
        return true;
    }

    // Security from bots

    // enable/disable works only once, token never returns to Locked
    function setBotProtection(bool enable) public onlyOwner() {
        if (enable && state == State.Locked) state = State.Restricted;
        if (!enable) state = State.Unlocked;
    }

    function setRestrictionAmount(uint256 amount) public onlyOwner() {
        maxRestrictionAmount = amount;
    }

    function blacklistAccount(address account, bool ban) public onlyOwner() {
        isBlacklisted[account] = ban;
    }

    modifier launchRestrict(address sender, address recipient, uint256 amount) {
        if (state == State.Locked) {
            require(sender == owner(), "Tokens are locked");
        }
        if (state == State.Restricted) {
            require(amount <= maxRestrictionAmount, "BNRG: amount greater than max limit in restricted mode");
            require(lastTx[sender].add(60) <= block.timestamp && lastTx[recipient].add(60) <= block.timestamp, "BMON: only one tx/min in restricted mode");
            lastTx[sender] = block.timestamp;
            lastTx[recipient] = block.timestamp;
        }
        if (state == State.Unlocked) {
            if (isBlacklisted[recipient]) {
                require(lastTx[recipient] + 30 days <= block.timestamp, "BNRG: only one tx/month in banned mode");
                lastTx[recipient] = block.timestamp;
            } else if (isBlacklisted[sender]) {
                require(lastTx[sender] + 30 days <= block.timestamp, "BNRG: only one tx/month in banned mode");
                lastTx[sender] = block.timestamp;
            }
        }
        _;
    }

    // Bot security end
}

