    ) = _getValues(tAmount);
    _rOwned[sender] = _rOwned[sender]-(rAmount);

    uint256 _recipientBalBefore = balanceOf(recipient);
    uint256 _deadBalBefore = balanceOf(deadAddress);

    _adjustTotalBalanceRefs(recipient, tTransferAmount, rTransferAmount);
