    );
    _takeLiquidityReflectAndEmitTransfers(
      sender,
      recipient,
      _recipientBalBefore,
      _deadBalBefore,
      rBurnAmount,
      tLiquidity,
      rFee,
      tFee
    );
  }

  function _transferFromExcluded(
