    _rOwned[recipient] = _rOwned[recipient]+(rRecipientAmount);
    _rOwned[deadAddress] = _rOwned[deadAddress]+(rBurnAmount);
    return (rBurnAmount, rRecipientAmount);
  }

  function _takeLiquidityReflectAndEmitTransfers(
