      emit Transfer(sender, marketingAddress, tOurShare);
    } else if(rOurShare > 0) {
      emit Transfer(sender, marketingAddress, tokenFromReflection(rOurShare));
    } 

    _rOwned[teamAddress] += rTeamShare;
    if (_isExcluded[teamAddress] && tTeamShare > 0) {
