      _tOwned[teamAddress] += tTeamShare;
    } else if(rTeamShare > 0) {
      emit Transfer(sender, teamAddress, tokenFromReflection(rTeamShare));
    }
  }

  function calculateTaxFee(uint256 _amount) private view returns (uint256) {
