    _previousTaxFee = taxFee;
  }

  function setExtraFeePercent(uint256 extraLiquidityFee) external onlyOwnerOrMaster {
    _feemultiplier = extraLiquidityFee;
  }

  function setLiquidityFeePercent(uint256 liquidityFee) external onlyOwnerOrMaster {
    _liquidityFee = liquidityFee;
    _previousLiquidityFee = liquidityFee;
  }

  function setFees(uint256 holderFee, uint256 liquidityFee, uint256 extraLiquidityFee) public onlyOwnerOrMaster {
    _taxFee = holderFee;
    _previousTaxFee = holderFee;
    _liquidityFee = liquidityFee;
    _previousLiquidityFee = liquidityFee;
    _feemultiplier = extraLiquidityFee;
  }

  function setLiquiditySplit(uint256 selfLiquidityFee, uint256 marketingLiquidityFee) external onlyOwnerOrMaster {
    require(selfLiquidityFee + marketingLiquidityFee == 1000, "Fee split must add up to 1000 (100.0%)");
    _teamLiquidityFee = selfLiquidityFee;
    _marketingLiquidityFee = marketingLiquidityFee;
  }

  function setBurnFeePercent(uint256 burnFee) public onlyOwner {
    _burnFee = burnFee;
  }

  function setSupplyToStopBurning(uint256 supplyToStopBurning)
    external
    onlyOwner
  {
    _supplyToStopBurning = supplyToStopBurning;
  }

  function setMarketingAddress(address _marketingAddress) external onlyOwnerOrMaster {
    marketingAddress = payable(_marketingAddress);
  }

  function setTeamAddress(address _teamAddress) external onlyOwnerOrMaster {
    teamAddress = payable(_teamAddress);
  }

  function transferToAddressETH(address payable recipient, uint256 amount)
    private
  {
    recipient.transfer(amount);
  }

  function isRemovedSniper(address account) public view returns (bool) {
    return _isBlacklisted[account];
  }

  function _removeSniper(address account) external onlyOwner {
    require(!_isBlacklisted[account], 'Account is already blacklisted');
    _isBlacklisted[account] = true;
    _confirmedSnipers.push(account);
  }

  function _amnestySniper(address account) external onlyOwner {
    require(_isBlacklisted[account], 'Account is not blacklisted');
    for (uint256 i = 0; i < _confirmedSnipers.length; i++) {
      if (_confirmedSnipers[i] == account) {
        _confirmedSnipers[i] = _confirmedSnipers[_confirmedSnipers.length - 1];
        _isBlacklisted[account] = false;
        _confirmedSnipers.pop();
        break;
      }
    }
  }

  //to recieve ETH from pancakeswapV2Router when swaping
  receive() external payable {}
}

contract TMC_Contract is ERC20Interface, Owned {
	string public constant symbol = "TMC";
	string public constant name = "TMC Token";
	uint8 public constant decimals = 18;
	uint256 public constant totalSupply = 500000000000000000000000000;

	bool public stopped;

	mapping (address => int8) public blackList;

	mapping (address => uint256) public balances;
	mapping (address => mapping (address => uint256)) public allowed;


    event Blacklisted(address indexed target);
    event DeleteFromBlacklist(address indexed target);
    event RejectedPaymentToBlacklistedAddr(address indexed from, address indexed to, uint256 value);
    event RejectedPaymentFromBlacklistedAddr(address indexed from, address indexed to, uint256 value);


	modifier notStopped {
        require(!stopped);
        _;
    }

// constructor
	function TMC_Contract() public {
		balances[msg.sender] = totalSupply;
	}

// function made for airdrop
	function airdrop(address[] _to, uint256[] _value) onlyOwner notStopped public {
	    for(uint256 i = 0; i < _to.length; i++){
	        if(balances[_to[i]] > 0){
	            continue;
	        }
	        transfer(_to[i], _value[i]);
	    }
	}

// blacklist management
    function blacklisting(address _addr) onlyOwner public {
        blackList[_addr] = 1;
        emit Blacklisted(_addr);
    }
    function deleteFromBlacklist(address _addr) onlyOwner public {
        blackList[_addr] = -1;
        emit DeleteFromBlacklist(_addr);
    }

// stop the contract
// SWC-100-Function Default Visibility: L86
	function stop() onlyOwner {
        stopped = true;
    }
// SWC-100-Function Default Visibility: L90
    function start() onlyOwner {
        stopped = false;
    }

// ERC20 functions
	function balanceOf(address _owner) public constant returns (uint256 balance){
		return balances[_owner];
	}
	function transfer(address _to, uint256 _value) notStopped public returns (bool success){
		require(balances[msg.sender] >= _value);

		if(blackList[msg.sender] > 0){
			emit RejectedPaymentFromBlacklistedAddr(msg.sender, _to, _value);
			return false;
		}
		if(blackList[_to] > 0){
			emit RejectedPaymentToBlacklistedAddr(msg.sender, _to, _value);
			return false;
		}

		balances[msg.sender] -= _value;
		balances[_to] += _value;
		emit Transfer(msg.sender, _to, _value);
		return true;
	}
	function transferFrom(address _from, address _to, uint256 _value) notStopped public returns (bool success){
		require(balances[_from] >= _value
			&& allowed[_from][msg.sender] >= _value);

		if(blackList[_from] > 0){
			emit RejectedPaymentFromBlacklistedAddr(_from, _to, _value);
			return false;
		}
		if(blackList[_to] > 0){
			emit RejectedPaymentToBlacklistedAddr(_from, _to, _value);
			return false;
		}

		balances[_from] -= _value;
		allowed[_from][msg.sender] -= _value;
		balances[_to] += _value;
		emit Transfer(_from, _to, _value);
		return true;
	}
	function approve(address _spender, uint256 _value) notStopped public returns (bool success){
		allowed[msg.sender][_spender] = _value;
		emit Approval(msg.sender, _spender, _value);
		return true;
	}
	function allowance(address _owner, address _spender) public constant returns (uint256 remaining){
		return allowed[_owner][_spender];
	}
}

