        _reimburse(_whom, 0xdeadbeef, _burn);
        Burn(_whom, _burn);
    }

    function reimburseForSubtask(
        address _requestor,
        address _provider,
        uint256 _amount,
        bytes32 _subtask_id
    )
        onlyConcent
        external
    {
        _reimburse(_requestor, _provider, _amount);
        ReimburseForSubtask(_requestor, _provider, _amount, _subtask_id);
    }

