        // Define and get amount of yield
        uint256 yieldAmount;
        if (duration == 12 weeks) {
            yieldAmount = (threeMonthPromille * amount) / 1000;
        } else if (duration == 24 weeks) {
            yieldAmount = (sixMonthPromille * amount) / 1000;
