    it's primarily used by the exchange but could be used by any other agent.
*/
contract Oracle is Ownable {
    uint256 public constant VERSION = 2;

    event NewSymbol(bytes32 _currency, string _ticker, uint8 _decimals);

    struct Symbol {
        string ticker;
        uint8 decimals;
        bool supported;
    }

